{
  "query": "query getMember($memberId: ID!) { getMember(memberId: $memberId) { _id memberNumber firstName lastName baptismalName gender startDate endDate email primaryPhone secondaryPhone paymentFlag created_date addresses { street street2 city state zip country created_date } memberFamilies { id firstName middleName lastName baptismalName gender relationShip createdDate } payment{amount,month,year,paymentMethod , paymentDate,createdDate} } }",
  "variables": {
    "memberId": "661fe6b83ba1df3fc1d6d89b"
  }
}

{
  "query": "{ getAllMembers { _id memberNumber firstName lastName baptismalName gender startDate endDate email primaryPhone secondaryPhone paymentFlag created_date addresses { street street2 city state zip country created_date } memberFamilies { id firstName middleName lastName baptismalName gender relationShip createdDate }payment{amount,month,year,paymentMethod , paymentDate,createdDate}  } }"
}

{
  "query": "mutation updateMember($memberId: ID!, $memberInput: MemberInput!) { updateMember(memberId: $memberId, memberInput: $memberInput) { _id memberNumber firstName lastName } }",
  "variables": {
    "memberId": "661d55105f6d84128813c667",
    "memberInput": {
      "memberNumber": "12345444",
      "firstName": "John",
      "middleName": "Middle",
      "lastName": "The Baptist",
      "baptismalName": "Baptismal",
      "gender": "Male",
      "startDate": "2024-04-15",
      "endDate": "2024-04-15",
      "email": "john.doe@example.com",
      "primaryPhone": "1234567890",
      "secondaryPhone": "",
      "paymentFlag": true
    }
  }
}

{
  "operationName": "addUser",
  "query": "mutation addUser($username: String!, $email: String!, $password: String!) { addUser(username: $username, email: $email, password: $password) { token user { _id username email } } }",
  "variables": {
    "username": "engda",
    "email": "engda@gmail.com",
    "password": "jesus"
  }
}

{
  "operationName": "login",
  "query": "mutation login($email: String!, $password: String!) { login(email: $email, password: $password) { token user { _id  email } } }",
  "variables": {
    "email": "engda@gmail.com",
    "password": "jesus"
  }
}
